import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import WeatherModal from '../components/WeatherModal';

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showWeather, setShowWeather] = useState(false);
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/catalog?search=${encodeURIComponent(searchTerm)}`);
    }
  };

  const handleInputChange = async (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value.length > 2) {
      try {
        const response = await fetch(`/api/search/suggestions?q=${value}`);
        const data = await response.json();
        setSuggestions(data);
      } catch (error) {
        console.error('Error fetching suggestions:', error);
      }
    } else {
      setSuggestions([]);
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold text-nature-blue mb-4">
          Bienvenue sur PêcheMaster Pro
        </h1>
        <p className="text-xl text-gray-600">
          Votre guide virtuel pour maîtriser les techniques de pêche
        </p>
      </div>

      <div className="bg-white p-6 rounded-lg shadow-lg mb-8">
        <form onSubmit={handleSearch} className="space-y-4">
          <div className="relative">
            <input
              type="text"
              value={searchTerm}
              onChange={handleInputChange}
              placeholder="Rechercher une technique, un poisson ou un lieu..."
              className="w-full p-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-nature-blue focus:border-transparent"
            />
            {suggestions.length > 0 && (
              <div className="absolute w-full bg-white border border-gray-200 rounded-lg mt-1 shadow-lg z-10">
                {suggestions.map((suggestion, index) => (
                  <div
                    key={index}
                    className="p-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => {
                      setSearchTerm(suggestion);
                      setSuggestions([]);
                    }}
                  >
                    {suggestion}
                  </div>
                ))}
              </div>
            )}
          </div>
          <button
            type="submit"
            className="w-full bg-nature-blue text-white py-3 rounded-lg hover:bg-blue-700 transition duration-200"
          >
            Rechercher
          </button>
        </form>
      </div>

      <div className="text-center">
        <button
          onClick={() => setShowWeather(true)}
          className="bg-nature-green text-white px-6 py-3 rounded-lg hover:bg-green-700 transition duration-200"
        >
          Voir la météo locale
        </button>
      </div>

      {showWeather && (
        <WeatherModal onClose={() => setShowWeather(false)} />
      )}
    </div>
  );
};

export default Home;
