import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Catalog = () => {
  const [techniques, setTechniques] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const searchTerm = searchParams.get('search') || '';

    const fetchTechniques = async () => {
      try {
        const response = await fetch(`/api/techniques${searchTerm ? `?search=${searchTerm}` : ''}`);
        const data = await response.json();
        setTechniques(data);
      } catch (error) {
        console.error('Error fetching techniques:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchTechniques();
  }, [location.search]);

  const getDifficultyColor = (difficulty) => {
    switch (difficulty.toLowerCase()) {
      case 'facile':
        return 'bg-green-100 text-green-800';
      case 'intermédiaire':
        return 'bg-yellow-100 text-yellow-800';
      case 'avancé':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-nature-blue"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto">
      <h1 className="text-3xl font-bold text-nature-blue mb-8">
        Catalogue des Techniques de Pêche
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {techniques.map((technique) => (
          <div
            key={technique._id}
            className="bg-white rounded-lg shadow-lg overflow-hidden cursor-pointer transform hover:scale-105 transition duration-200"
            onClick={() => navigate(`/technique/${technique._id}`)}
          >
            <img
              src={technique.imageUrl}
              alt={technique.name}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h2 className="text-xl font-bold mb-2">{technique.name}</h2>
              <p className="text-gray-600 mb-4 line-clamp-2">
                {technique.description}
              </p>
              <div className="flex items-center justify-between">
                <span
                  className={`px-3 py-1 rounded-full text-sm ${getDifficultyColor(
                    technique.difficulty
                  )}`}
                >
                  {technique.difficulty}
                </span>
                <div className="text-sm text-gray-500">
                  {technique.equipment.length} équipements
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {techniques.length === 0 && (
        <div className="text-center py-12">
          <p className="text-gray-600">
            Aucune technique trouvée. Essayez d'autres termes de recherche.
          </p>
        </div>
      )}
    </div>
  );
};

export default Catalog;
