import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaSearch, FaFish, FaBookOpen, FaMapMarkerAlt, FaRegClock } from 'react-icons/fa';
import { WiDaySunny, WiStrongWind } from 'react-icons/wi';
import { format } from 'date-fns';
import { fr } from 'date-fns/locale';
import weatherImg from '../assets/images/weather.jpeg';
import fishingTechniqueImg from '../assets/images/fishin-technique.jpeg';
import lureFishingImg from '../assets/images/lure-fishing.jpeg';

// Image de fond du hero
const HERO_BG = 'https://images.pexels.com/photos/5560911/pexels-photo-5560911.jpeg';

const modules = [
  {
    id: 1,
    title: 'Guide des Poissons',
    description: 'Découvrez les différentes espèces de poissons.',
    image: lureFishingImg,
    link: '/fish-guide',
    icon: <FaFish />
  },
  {
    id: 2,
    title: 'Techniques de Pêche',
    description: 'Apprenez les techniques de pêche.',
    image: fishingTechniqueImg,
    link: '/catalog',
    icon: <FaBookOpen />
  },
  {
    id: 3,
    title: 'Météo & Conditions',
    description: 'Consultez la météo pour la pêche.',
    image: weatherImg,
    link: '/weather',
    icon: <WiDaySunny />
  }
];

const quickLinks = [
  { id: 1, title: 'Top 10 Poissons', link: '/top-fish', icon: <FaFish /> },
  { id: 2, title: 'Guide Débutant', link: '/beginner', icon: <FaBookOpen /> },
  { id: 3, title: 'Spots Populaires', link: '/spots', icon: <FaMapMarkerAlt /> },
  { id: 4, title: 'Réglementation', link: '/rules', icon: <FaRegClock /> }
];

const featuredFish = {
  name: 'Brochet',
  description: 'Prédateur emblématique de nos eaux douces',
  image: lureFishingImg,
  period: 'Meilleure période : Automne'
};

const seasonalTips = [
  'Privilégiez la pêche en début de matinée',
  'Les poissons sont plus actifs près des berges',
  'Utilisez des leurres de surface'
];

function Home() {
  const [searchTerm, setSearchTerm] = useState('');
  const currentDate = format(new Date(), "d MMMM yyyy", { locale: fr });

  return (
    <div className="home-page">
      {/* Hero Section */}
      <div className="hero-section" style={{ backgroundImage: `url(${HERO_BG})` }}>
        <div className="hero-content">
          <h1>PêcheMaster Pro</h1>
          <p>Votre guide complet pour la pêche en eau douce</p>
          <Link to="/fish-guide" className="cta-button">
            Explorer les espèces
          </Link>
        </div>
      </div>

      {/* Search Section */}
      <div className="search-section">
        <div className="container">
          <div className="search-bar">
            <FaSearch className="search-icon" />
            <input
              type="text"
              placeholder="Rechercher une espèce, une technique..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <div className="popular-searches">
            <span>Recherches populaires : </span>
            <Link to="/search?q=brochet">Brochet</Link>
            <Link to="/search?q=mouche">Pêche à la mouche</Link>
            <Link to="/search?q=carpe">Carpe</Link>
          </div>
        </div>
      </div>

      {/* Main Modules */}
      <div className="container">
        <div className="row modules-row">
          {modules.map((module) => (
            <div key={module.id} className="col-md-4">
              <Link to={module.link} className="text-decoration-none">
                <div className="module-card">
                  <div className="module-image-container">
                    <img
                      src={module.image}
                      alt={module.title}
                      className="module-image"
                    />
                  </div>
                  <div className="module-content">
                    <div className="module-icon">{module.icon}</div>
                    <h3 className="module-title">{module.title}</h3>
                    <p className="module-description">{module.description}</p>
                  </div>
                </div>
              </Link>
            </div>
          ))}
        </div>

        {/* Weather Widget & Featured Fish */}
        <div className="row mt-4">
          <div className="col-md-4">
            <div className="weather-widget">
              <h3>Météo Locale</h3>
              <div className="weather-info">
                <WiDaySunny className="weather-icon" />
                <div className="weather-details">
                  <p>22°C</p>
                  <p><WiStrongWind /> 12 km/h</p>
                  <p>Pression: 1015 hPa</p>
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-4">
            <div className="featured-fish">
              <h3>Poisson du Mois</h3>
              <img src={featuredFish.image} alt={featuredFish.name} />
              <h4>{featuredFish.name}</h4>
              <p>{featuredFish.description}</p>
              <span>{featuredFish.period}</span>
            </div>
          </div>
          <div className="col-md-4">
            <div className="seasonal-tips">
              <h3>Conseils de Saison</h3>
              <p className="current-date">{currentDate}</p>
              <ul>
                {seasonalTips.map((tip, index) => (
                  <li key={index}>{tip}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>

        {/* Quick Links */}
        <div className="quick-links">
          <h3>Accès Rapide</h3>
          <div className="quick-links-grid">
            {quickLinks.map((link) => (
              <Link key={link.id} to={link.link} className="quick-link-item">
                {link.icon}
                <span>{link.title}</span>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
