import React from 'react';
import { Link } from 'react-router-dom';
import fishGuideImg from '../assets/images/fish-guide.jpg';
import techniquesImg from '../assets/images/techniques.jpg';
import weatherImg from '../assets/images/weather.jpg';

const modules = [
  {
    id: 1,
    title: 'Guide des Poissons',
    description: 'Découvrez les différentes espèces de poissons et leurs caractéristiques.',
    image: fishGuideImg,
    link: '/fish-guide'
  },
  {
    id: 2,
    title: 'Techniques de Pêche',
    description: 'Apprenez les différentes techniques de pêche, du niveau débutant à expert.',
    image: techniquesImg,
    link: '/catalog'
  },
  {
    id: 3,
    title: 'Météo & Conditions',
    description: 'Consultez les conditions météorologiques idéales pour la pêche.',
    image: weatherImg,
    link: '/weather'
  }
];

function Home() {
  return (
    <div className="container py-5">
      <div className="text-center mb-5">
        <h1 className="display-4 mb-3">PêcheMaster Pro</h1>
        <p className="lead">Votre guide complet pour devenir un expert de la pêche</p>
      </div>

      <div className="row">
        {modules.map((module) => (
          <div key={module.id} className="col-md-4 mb-4">
            <Link to={module.link} className="text-decoration-none">
              <div className="card h-100 shadow-sm">
                <img
                  src={module.image}
                  alt={module.title}
                  className="card-img-top"
                  style={{ height: '200px', objectFit: 'cover' }}
                />
                <div className="card-body">
                  <h3 className="card-title h5 text-primary">{module.title}</h3>
                  <p className="card-text text-muted">{module.description}</p>
                </div>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
