import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaSearch, FaFilter } from 'react-icons/fa';

const MOCK_TECHNIQUES = [
  {
    id: '1',
    name: 'Pêche au coup',
    difficulty: 'Facile',
    description: 'La pêche au coup est une technique de pêche très populaire, particulièrement adaptée aux débutants.',
    imageUrl: 'https://cdn.pixabay.com/photo/2020/05/13/21/36/fishing-5168953_1280.jpg'
  },
  {
    id: '2',
    name: 'Pêche aux leurres',
    difficulty: 'Intermédiaire',
    description: 'La pêche aux leurres est une technique active qui consiste à attraper les poissons carnassiers en imitant leurs proies naturelles.',
    imageUrl: 'https://cdn.pixabay.com/photo/2017/08/04/14/47/fishing-2580210_1280.jpg'
  },
  {
    id: '3',
    name: 'Pêche à la mouche',
    difficulty: 'Difficile',
    description: 'La pêche à la mouche est une technique élégante qui imite les insectes dont se nourrissent les poissons.',
    imageUrl: 'https://cdn.pixabay.com/photo/2015/03/26/09/41/fishing-690441_1280.jpg'
  }
];

const Catalog = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedDifficulty, setSelectedDifficulty] = useState('');
  const [isFilterOpen, setIsFilterOpen] = useState(false);

  const filteredTechniques = MOCK_TECHNIQUES.filter(technique => {
    const matchesSearch = technique.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         technique.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDifficulty = !selectedDifficulty || technique.difficulty === selectedDifficulty;
    return matchesSearch && matchesDifficulty;
  });

  const difficulties = [...new Set(MOCK_TECHNIQUES.map(technique => technique.difficulty))];

  const getDifficultyColor = (difficulty) => {
    switch (difficulty) {
      case 'Facile':
        return 'bg-green-100 text-green-800';
      case 'Intermédiaire':
        return 'bg-yellow-100 text-yellow-800';
      case 'Difficile':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="home-container">
      <div className="container py-5">
        <h1 className="text-center text-white mb-5">Catalogue des Techniques de Pêche</h1>
        <div className="row">
          {filteredTechniques.map((technique) => (
            <div key={technique.id} className="col-md-4 mb-4">
              <div className="technique-card">
                <img 
                  src={technique.imageUrl} 
                  alt={technique.name}
                  className="technique-image"
                />
                <h2 className="technique-title">{technique.name}</h2>
                <p className="technique-description">{technique.description}</p>
                <div className="d-flex justify-content-between align-items-center">
                  <span className={`difficulty-badge difficulty-${technique.difficulty.toLowerCase()}`}>
                    {technique.difficulty}
                  </span>
                  <Link to={`/catalog/${technique.id}`} className="plus-info-link">
                    Plus d'informations →
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Catalog;
